export class Mocker<F, R> {
  cacheFn: F | null;
  cacheReturnValue: R;

  constructor(value: R) {
    this.cacheFn = null;
    this.cacheReturnValue = value;
  }

  returnValue(value: R): void {
    this.cacheFn = null;
    this.cacheReturnValue = value;
  }

  fn(f: F): void {
    this.cacheFn = f;
  }
}
