import { Mocker } from '../mocker';

type F_GetBlockNumber = () => u64;
type R_GetBlockNumber = returnof<F_GetBlockNumber>;
class GetBlockNumberMocker extends Mocker<F_GetBlockNumber, R_GetBlockNumber> {}
export const getBlockNumber: GetBlockNumberMocker = new GetBlockNumberMocker(888);

type F_GetBlockTimeStamp = () => u64;
type R_GetBlockTimeStamp = returnof<F_GetBlockTimeStamp>;
class GetBlockTimeStampMocker extends Mocker<F_GetBlockTimeStamp, R_GetBlockTimeStamp> {}
export const getBlockTimeStamp: GetBlockTimeStampMocker = new GetBlockTimeStampMocker(
  1594224000000,
);

type F_GetBlockHash = (blockNumber: u64) => string;
type R_GetBlockHash = returnof<F_GetBlockHash>;
class GetBlockHashMocker extends Mocker<F_GetBlockHash, R_GetBlockHash> {}
export const getBlockHash: GetBlockHashMocker = new GetBlockHashMocker(
  '1234567890123456789012345678901234567890123456789012345678901234',
);
