import { getBlockNumber, getBlockHash } from './block';

describe('chain/block', () => {
  test('getBlockNumber', () => {
    expect(getBlockNumber()).toBe(666);
  });

  test('getBlockHash', () => {
    expect(getBlockHash(123)).toBe(
      '1234567890123456789012345678901234567890123456789012345678901234',
    );
  });
});
