import { getInterfaceName, getInterfaceParamBytes, setReturnValue } from './interface';
import { utils } from '..';

describe('chain/interface', () => {
  test('getInterfaceName', () => {
    expect(getInterfaceName()).toBe('interface-name');
  });

  test('getInterfaceParamBytes', () => {
    expect(getInterfaceParamBytes()).toStrictEqual(utils.getBytes('interface-params'));
  });

  test('setReturnValue', () => {
    expect(() => {
      setReturnValue('myfish');
    }).toThrow();
  });
});
