import { ENV, utils } from '..';

// 跑单测时 println 的内容会被替换成调用 asp 的 log 函数
// 这样会和 general 文件中的 log 命名冲突，所以拎出来单独写一个文件

/**
 * 打印日志，仅用于调试
 * @param value 要打印的值
 */
export function println<T>(value: T): void {
  const str = utils.getDisplayString(value);
  const bytes = utils.getBytes(`🐟 ${str}`);
  ENV.println(bytes.dataStart as u32, bytes.length);
}
