const debug = require('debug')('myassembly:tester:Log');

module.exports =
  ({ __getString }) =>
  (user_custom_ptr, user_custom_length, topics_ptr, topics_length) => {
    const custom = __getString(user_custom_ptr, user_custom_length);
    const topics = __getString(topics_ptr, topics_length);
    debug(custom, topics);

    // TODO: need to support more types
    console.log(`[${topics}] ${custom}`);
    return 0;
  };
